mapper.update:
UPDATE CHALL_TABLE
SET CHALL_START_TIME = #{challStartTime},
CHALL_END_TIME = #{challEndTime},
CHALL_STATE = #{challState}
WHERE CHALL_IDX = #{challIdx}

mapper.select:
SELECT CT.CHALL_IDX, CT.CHALL_TITLE, CT.CHALL_DESCRIPTION, CT.CHALL_CATEGORY_IDX, CT.MAX_PARTICIPANTS, CT.CHALL_END_TIME, COUNT(CJ.USER_IDX) AS CURRENT_PARTICIPANTS, CT.CHALL_CREATED_AT, CT.CHALL_STATE FROM CHALL_TABLE CT LEFT JOIN CHALL_JOIN CJ ON CT.CHALL_IDX = CJ.CHALL_IDX
AND CT.CHALL_STATE = #{challState}
AND CT.CHALL_CATEGORY_IDX = #{challCategoryIdx}
AND UPPER(CT.VISIBILITY_TYPE) = UPPER(#{visibilityType})
AND UPPER(CT.CHALLENGE_TYPE) = UPPER(#{challengeType})
AND CT.CHALL_END_TIME IS NOT NULL AND CT.CHALL_END_TIME < NOW()
AND (CT.CHALL_END_TIME IS NULL OR CT.CHALL_END_TIME > NOW())
AND (CT.CHALL_END_TIME IS NULL OR CT.CHALL_END_TIME > NOW())
GROUP BY CT.CHALL_IDX, CT.CHALL_TITLE, CT.CHALL_DESCRIPTION, CT.CHALL_CATEGORY_IDX, CT.MAX_PARTICIPANTS, CT.CHALL_END_TIME, CT.CHALL_CREATED_AT, CT.CHALL_STATE ORDER BY CT.CHALL_CREATED_AT DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM CHALL_TABLE CT



        AND CT.CHALL_STATE = #{challState}




        AND CT.CHALL_CATEGORY_IDX = #{challCategoryIdx}




        AND UPPER(CT.VISIBILITY_TYPE) = UPPER(#{visibilityType})




        AND CT.CHALLENGE_TYPE = #{challengeType}






            AND CT.CHALL_END_TIME IS NOT NULL
            AND CT.CHALL_END_TIME <= NOW()




            AND (CT.CHALL_END_TIME IS NULL OR CT.CHALL_END_TIME > NOW())




            AND (CT.CHALL_END_TIME IS NULL OR CT.CHALL_END_TIME > NOW())

SELECT COUNT(*)
FROM CHALL_JOIN
WHERE CHALL_IDX = #{challIdx}

mapper.insert:
INSERT INTO VERIFY_TABLE (
    CHALL_IDX, USER_IDX,  VERIFY_DATE, START_TIME, END_TIME, ELAPSED_TIME)
VALUES (
    #{challIdx}, #{userIdx}, CURDATE(), #{startTime}, #{endTime}, #{elapsedTime}
)

mapper.select:
SELECT MIN_PARTICIPATION_TIME
FROM CHALL_TABLE
WHERE CHALL_IDX = #{challIdx}

mapper.update:
UPDATE CHALL_JOIN
SET ACTIVITY = #{activity}
WHERE CHALL_IDX = #{challIdx}
AND USER_IDX = #{userIdx}

mapper.select:
SELECT COUNT(*)
FROM VERIFY_TABLE
WHERE USER_IDX = #{userIdx}
AND CHALL_IDX = #{challIdx}
AND VERIFY_DATE = #{today}

mapper.update:
UPDATE CHALL_TABLE
SET TOTAL_CLEAR_TIME = GREATEST(TOTAL_CLEAR_TIME - #{elapsedTime}, 0)
WHERE CHALL_IDX = #{challIdx}

mapper.select:
SELECT COALESCE(SUM(ELAPSED_TIME), 0)
FROM VERIFY_TABLE
WHERE CHALL_IDX = #{challIdx}

SELECT TOTAL_CLEAR_TIME
FROM CHALL_TABLE
WHERE CHALL_IDX = #{challIdx}

mapper.update:
UPDATE CHALL_TABLE
SET CHALL_STATE = 'COMPLETED'
WHERE CHALL_IDX = #{challIdx}
